function Time(time){
    var now = +new Date;
    if (time == undefined) return now;
    time = +new Date(time);
    if (isNaN(time)) return now;
    return time;
}
function leftPad(str,padding,length){
    str = '' + str;
    if (str.length >= length){
        return str;
    }
    for (var i=str.length;i<length;i++){
        str = padding + str;
    }
    return str;
}

Time.prototype.date = function (){
    return new Date(this.val());
}

Time.prototype.format = function (template){
    var time = new Date(this.val())
    var day = template || "yyyy-MM-dd hh:mm:ss";
    day = day.replace(/(y+)/,function ($,$1){
        return leftPad(time.getFullYear(),0,$1.length)
    });
    day = day.replace(/(M+)/,function ($,$1){
        return leftPad(time.getMonth() + 1,0,$1.length)
    });
    day = day.replace(/(d+)/,function ($,$1){
        return leftPad(time.getDate(),0,$1.length)
    });
    day = day.replace(/(h+)/,function ($,$1){
        return leftPad(time.getHours(),0,$1.length)
    });
    day = day.replace(/(m+)/,function ($,$1){
        return leftPad(time.getMinutes(),0,$1.length)
    });
    day = day.replace(/(s+)/,function ($,$1){
        return leftPad(time.getSeconds(),0,$1.length)
    });
    return day;
}

Time.prototype.toString = function (){
    return this.date().toString();
}



function isBase64(str){
    if (!str) return false;
    var len = str.length;
    if (!len || len % 4 !== 0 || /[^A-Z0-9+\/=]/i.test(str)){
        return false;
    }
    var firstPaddingChar = str.indexOf('=');
    return firstPaddingChar === -1 || firstPaddingChar === len - 1 || firstPaddingChar === len - 2 && str[len - 1] === '=';
}

function Base64(buffer){
    if (Buffer.isBuffer(buffer)){
        return new Buffer(buffer).toString("base64");
    }
    if (typeof buffer == "string"){
        if (/http/i.test(buffer)){
            return buffer;
        }else if (isBase64(buffer)){
            return buffer;
        }else {
            return new Buffer(buffer).toString("base64");
        }
    }
    return "";
}

Base64.prototype.toBuffer = function (){
    return new Buffer(this.val(),"base64");
}


var crypto = require('crypto');

function UUID(input){
    if (input.match(/[0-9a-z]{8}-[0-9a-z]{4}-[0-9a-z]{4}-[0-9a-z]{4}-[0-9a-z]{12}/)){
        return input;
    }
    var hash = crypto.createHash("sha256").update(input,'utf8').digest("hex");
    var lens = [8,4,4,4,12];
    var array = [],offset=0;
    lens.forEach(function (len){
        array.push(hash.substring(offset,offset+len));
        offset += len;
    });
    return array.join("-");
}


function Id(string){
    string = string.replace(/\/$/,'').split('/').pop().replace(/\W/g,"");
    return leftPad(string,'0',5);
}


function IsEnd(value){
    if (value == undefined){
        return false;
    }else if (typeof value == "boolean"){
        return value;
    }else if (typeof value == "number"){
        return value > 0;
    }else if (typeof value == "string"){
        return /完成|完结|完本|终章|结局|全本|结束|结尾|末尾|末章/.test(value);
    }
    return false;
}


function Link(object){
    if (object == undefined) return '';
    if (typeof object === "string") return object;
    if (Array.isArray(object)){
        return object.map(arguments.callee).join('|');
    }
    object.method = (object.method || 'GET').toUpperCase();
    object.url = object.url || object.href || object.src;
    if (object.method == 'GET') return object.url;
    return JSON.stringify(object);
}

Link.prototype.toString = function (){
    var link = this.val();
    if (Array.isArray(link)){
        link = link.filter(x=>x.url);
        return JSON.stringify(this.val());
    }
    if (!link.url) return '';
    if (link.method == 'GET') return link.url;
    return JSON.stringify(link);
}


function toReStr(str) {
    return str.replace(/[()\[\]{}|+.,^$?\\*]/g, "\\$&");
}

var toString = Object.prototype.toString;
function isRegExp(value){
    return toString.call(value) === "[object RegExp]"
}

function isObject(value){
    return toString.call(value) === "[object Object]"
}

function isFunction(value){
    return toString.call(value) === "[object Function]"
}

function isString(value){
    return toString.call(value) === "[object String]"
}
function replace(value,selector){
    if (!value) return value;
    if (isFunction(selector)){
        value = selector(value);
        return value;
    }
    if (isString(selector)){
        try{
            var regexp = new RegExp(selector,"gi");
        }catch (e){
            var regexp = new RegExp(toReStr(selector),"gi");
        }
        return replace(value,regexp);
    }
    if (Array.isArray(selector)){
        selector.forEach(x=>{
            value = replace(value,x);
        });
        return value;
    }
    if (isRegExp(selector)){
        value = value.replace(selector,"");
        return value;
    }
    if (isObject(selector)){
        Object.keys(selector).forEach(function (k){
            try{
                var regexp = new RegExp(k,"gi");
            }catch (e){
                var regexp = new RegExp(toReStr(k),"gi");
            }
            value = value.replace(regexp,selector[k]);
        });
        return value;
    }
    return value;
};

