function filterTag(text){
    text = text.replace(/<(br|address|blockquote|center|dir|div|dl|fieldset|form|h1|h2|h3|h4|h5|h6|hr|isindex|menu|noframes|noscript|ol|p|pre|table|ul)[^<>]*?>/g,"\n");
    text = text.replace(/(<[^<>]*?>)/g,function($,$1){
        if ($1.match(/<a|<img|<\/a|<base|<link|<base/))return $1;
        return '';
    });
    return text;
}

//清除标题
function clearTitle(text){
    text = text.replace(/([\(（\[【].*?[\)）\]】])/gi,function ($,$1){
        //if (text.indexOf($1) == 0) return $1;
        if ($1.match(/月票|推荐票|推荐|订阅|支持|收藏|加更|字数/)) return "";
        if ($1.match(/[1-9一二两三四五六七八九十○零百千万亿〇１２３４５６７８９０]更/)) return "";
        if ($1.match(/[二两三四五六七八九十]合一/))return "";
        //if (!$1.match(/[上中下续章回卷节折篇幕集部1-9一二两三四五六七八九十○零百千万亿〇１２３４５６７８９０]/))return "";
        return $1;
    });
    return replace(text,['《','》']);
}


